class Solution:
    def findClosestNumber(self, nums: list[int]) -> int:
        nums.sort(reverse=True)
        closest = float('inf')
        for n in nums:
            if abs(n) < closest:
                closest = n
            elif abs(n) == closest:
                continue
            else:
                break
        return closest

