from collections import Counter

class Solution:
    def longestPalindrome(self, s: str) -> int:
        number_of_chars = list(Counter(s).values())
        ans = max((num for num in number_of_chars if num % 2), default=0)
        if ans:
            number_of_chars.remove(ans)
        for num in number_of_chars:
            if num % 2:
                ans += num - 1
            else:
                ans += num
        return ans

